actionConfiguration.body:
() => {
  try {
    const pod = update_pod_pod.selectedOptionValue;
    update_pod_employees.selectedOptionValues.forEach(empId => update_emp_pod.run({
      pod: pod,
      id: empId
    }));
    fetch_all_emp.run(() => {
      resetWidget('empTable', true);
      showAlert('POD updated for the selected employees', 'success');
    });
  } catch {
    showAlert('POD update seems to be broken right now, please try again after sometime', 'error');
  } finally {
    resetWidget('update_pod_modal', true);
    closeModal('update_pod_modal');
  }
}

