actionConfiguration.body:
() => {
  const birthdayImages = ['https://everydaypower.com/wp-content/uploads/2017/04/Birthday-Quotes-To-Celebrate-Life-Age-and-Friendships.jpg', 'https://cdn.pixabay.com/photo/2016/11/18/15/46/birthday-1835443__340.jpg'];
  const anniversaryImages = ['https://media.istockphoto.com/id/1309237361/photo/card-with-happy-anniversary-words-made-from-mixed-vivid-colored-wooden-letters-on-a-textured.jpg?s=612x612&w=0&k=20&c=NraAjHeA6YXfARnKVrjV7-_oCQ2Pf1qV5UATrs7ClXQ=', 'https://blog.bonus.ly/hubfs/make-work-anniversaries-meaningful.png'];
  const welcomeImage = ['https://elearningindustry.com/wp-content/uploads/2021/10/a-successful-remote-onboarding-guide.jpg', 'https://st2.depositphotos.com/3591429/6308/i/600/depositphotos_63081591-stock-photo-hands-holding-word-welcome.jpg'];
  let celebrations = '';
  _.filter(fetch_employees.data, e => moment(e.birthday, 'DD/MM/YY').format('DD-MM').match(moment().format('DD-MM'))).forEach(e => {
    celebrations = celebrations + `<div class="container">
  <img src="${birthdayImages[_.random(birthdayImages.length)]}">
  <div class="top"> Happy Birthday ${e.name}</div></div>`;
  });
  _.filter(_.filter(fetch_employees.data, e => !moment(e.start_date, 'DD/MM/YYYY').format('DD-MM-YYYY').match(moment().format('DD-MM-YYYY'))), e => moment(e.start_date, 'DD/MM/YY').format('DD-MM').match(moment().format('DD-MM'))).forEach(e => {
    celebrations = celebrations + `<div class="container">
  <img src="${anniversaryImages[_.random(anniversaryImages.length)]}">
  <div class="top"> Happy Anniversary ${e.name}</div></div>`;
  });
  _.filter(fetch_employees.data, e => moment(e.start_date, 'DD/MM/YYYY').format('DD-MM-YYYY').match(moment().format('DD-MM-YYYY'))).forEach(e => {
    celebrations = celebrations + `<div class="container">
  <img src="${welcomeImage[_.random(welcomeImage.length)]}">
  <div class="top"> Welcome To Appsmith ${e.name}</div></div>`;
  });
  return celebrations;
}

