actionConfiguration.body:
() => {
  let celeb = `{Heading}<div class="container">{image}{TextOnImage}</div>{context}{url}`;
  celeb = notification_heading.text == '' ? celeb.replace('{Heading}', '') : celeb.replace('{Heading}', `<h2 style='font-family:sans-serif;'> ${notification_heading.text}</h2>`);
  celeb = notification_Context.text == '' ? celeb.replace('{context}', '') : celeb.replace('{context}', `<p style='font-family:sans-serif;'> ${notification_heading.text}</p>`);
  celeb = notification_filepicker.files.length > 0 ? celeb.replace('{image}', `<img src="https://www.shutterstock.com/image-photo/surreal-image-african-elephant-wearing-260nw-1365289022.jpg">`) : celeb.replace('{image}', '');
  celeb = notification_text_on_picture.text == '' ? celeb.replace('{TextOnImage}', '') : celeb.replace('{TextOnImage}', `<div class="top">${notification_text_on_picture.text}</div>`);
  celeb = notification_link.text == '' ? celeb.replace('{url}', '') : celeb.replace('{url}', `<a href="${notification_link.text}">Click Here!</a>`);
  return celeb;
}

